function calcsize(e){e.preventDefault();var t=document.getElementById("currentwidth").value,n=document.getElementById("currentheight").value,i=document.getElementById("widthheight").value,d=document.getElementById("newsize").value;if("width"===i){var u=(n/t*d).toFixed(0);document.getElementById("result").innerHTML="the new height is: <span>"+u+"</span>"}if("height"===i){var h=(t/n*d).toFixed(0);document.getElementById("result").innerHTML="the new width is: <span>"+h+"</span>"}}